class Solution:
    def minWindow(self, s: str, t: str) -> str:
        len_s = len(s)
        min_window = None

        p1 = 0
        while p1 < len_s:
            if s[p1] in t:
                p1_cp = p1
                p1 += 1
                left = [x for x in str(t)]
                for p2 in range(p1_cp, len_s):  # there's always going to be next element due to the N-p1<len(t) check.
                    if s[p2] in t:
                        if s[p2] in left:
                            if p1+1 == p1_cp:
                                p1 = p2
                            left.remove(s[p2])

                    # found all different letters
                    if len(left) == 0:
                        if min_window is None or p2 - p1_cp < min_window[1] - min_window[0]:
                            min_window = (p1_cp, p2)
                        break
            else:
                p1 += 1

        if min_window is None:
            return ""
        return s[min_window[0]:min_window[1] + 1]


if __name__ == "__main__":
    s = Solution()
    print(s.minWindow("ADOBECODEBANC", "ABC"))
    print(s.minWindow("a", "a"))
    print(s.minWindow("aa", "aa"))
    print(s.minWindow("abxxaca", "xac"))

    s5 = ""
    t5 = ""
    print(s.minWindow(s5, t5))